mapper.insert:
INSERT INTO YAGL_YA_BWJW(uuid,yabh,yasxh,yabbh,yalx,yamc,yajb,dxid,dxmc,bwid,bwmc,sfkqy,jgid,jgbm,jgmc,zzrid,zzrmc,zzsj,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,delete_flag,datasource,bz,jdh,sjc,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.uuid,jdbcType=VARCHAR} uuid,#{item.yabh,jdbcType=VARCHAR} yabh,#{item.yasxh,jdbcType=VARCHAR} yasxh,#{item.yabbh,jdbcType=VARCHAR} yabbh,#{item.yalx,jdbcType=VARCHAR} yalx,#{item.yamc,jdbcType=VARCHAR} yamc,#{item.yajb,jdbcType=VARCHAR} yajb,#{item.dxid,jdbcType=VARCHAR} dxid,#{item.dxmc,jdbcType=VARCHAR} dxmc,#{item.bwid,jdbcType=VARCHAR} bwid,#{item.bwmc,jdbcType=VARCHAR} bwmc,#{item.sfkqy,jdbcType=VARCHAR} sfkqy,#{item.jgid,jdbcType=VARCHAR} jgid,#{item.jgbm,jdbcType=VARCHAR} jgbm,#{item.jgmc,jdbcType=VARCHAR} jgmc,#{item.zzrid,jdbcType=VARCHAR} zzrid,#{item.zzrmc,jdbcType=VARCHAR} zzrmc,#{item.zzsj,jdbcType=DATE} zzsj,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,'N' delete_flag,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.bz,jdbcType=VARCHAR} bz,#{item.jdh,jdbcType=VARCHAR} jdh,#{item.sjc,jdbcType=DATE} sjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE YAGL_YA_BWJW
uuid=#{uuid}, yabh=#{yabh}, yasxh=#{yasxh}, yabbh=#{yabbh}, yalx=#{yalx}, yamc=#{yamc}, yajb=#{yajb}, dxid=#{dxid}, dxmc=#{dxmc}, bwid=#{bwid}, bwmc=#{bwmc}, sfkqy=#{sfkqy}, jgid=#{jgid}, jgbm=#{jgbm}, jgmc=#{jgmc}, zzrid=#{zzrid}, zzrmc=#{zzrmc}, zzsj=TO_DATE(#{zzsj},'yyyy-MM-dd HH24:mi:ss'), cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=TO_DATE(#{xgsj},'yyyy-MM-dd HH24:mi:ss'), delete_flag='N', datasource=#{datasource}, bz=#{bz}, jdh=#{jdh}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE PKID = #{pkid}
